#!/bin/bash

# Cobean Backend 服务检测脚本
# 用于检测服务是否正常启动和运行

# 颜色定义
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# 配置
SERVICE_PORT=3000
SERVICE_NAME="Cobean Backend"
HEALTH_ENDPOINT="http://localhost:${SERVICE_PORT}/health"

# 日志函数
log_info() {
    echo -e "${BLUE}[INFO]${NC} $1"
}

log_success() {
    echo -e "${GREEN}[SUCCESS]${NC} $1"
}

log_warning() {
    echo -e "${YELLOW}[WARNING]${NC} $1"
}

log_error() {
    echo -e "${RED}[ERROR]${NC} $1"
}

# 检查端口是否被占用
check_port() {
    log_info "检查端口 ${SERVICE_PORT} 是否被占用..."
    
    local port_info=$(lsof -i :${SERVICE_PORT} 2>/dev/null)
    if [ -n "$port_info" ]; then
        log_success "端口 ${SERVICE_PORT} 正在被使用"
        echo "$port_info"
        return 0
    else
        log_error "端口 ${SERVICE_PORT} 未被占用"
        return 1
    fi
}

# 检查进程
check_process() {
    log_info "检查 ${SERVICE_NAME} 进程..."
    
    local process_info=$(ps aux | grep "node.*index.js\|npm start" | grep -v grep)
    if [ -n "$process_info" ]; then
        log_success "找到 ${SERVICE_NAME} 进程"
        echo "$process_info"
        return 0
    else
        log_error "未找到 ${SERVICE_NAME} 进程"
        return 1
    fi
}

# 检查健康状态
check_health() {
    log_info "检查服务健康状态..."
    
    local health_response=$(curl -s -w "%{http_code}" -o /tmp/health_response.json "${HEALTH_ENDPOINT}" 2>/dev/null)
    local http_code="${health_response: -3}"
    
    if [ "$http_code" = "200" ]; then
        log_success "服务健康检查通过"
        echo "响应内容:"
        cat /tmp/health_response.json 2>/dev/null | python3 -m json.tool 2>/dev/null || cat /tmp/health_response.json
        rm -f /tmp/health_response.json
        return 0
    else
        log_error "服务健康检查失败 (HTTP状态码: $http_code)"
        return 1
    fi
}

# 检查API端点
check_api_endpoints() {
    log_info "检查主要API端点..."
    
    local endpoints=(
        "/health"
        "/api/books"
        "/api/auth/status"
    )
    
    for endpoint in "${endpoints[@]}"; do
        local url="http://localhost:${SERVICE_PORT}${endpoint}"
        local response=$(curl -s -w "%{http_code}" -o /dev/null "${url}" 2>/dev/null)
        local http_code="${response: -3}"
        
        if [ "$http_code" = "200" ] || [ "$http_code" = "401" ]; then
            log_success "端点 ${endpoint} 可访问 (状态码: $http_code)"
        else
            log_warning "端点 ${endpoint} 不可访问 (状态码: $http_code)"
        fi
    done
}

